insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(1,15,'Hyatt Regency Danang Resort and Spa',2,'Hyatt Regency Danang Resort & Spa là khu nghỉ dưỡng được du khách đánh giá cao tại Đà Nẵng. Với các dịch vụ đa dạng, phục vụ đầy đủ nhu cầu thiết yếu của du khách tới thăm, có lẽ không điểm lưu trú nào lý tưởng bằng Hyatt Regency Danang Resort & Spa. Bên cạnh đó, trang thiết bị nội thất và khuôn viên của khách sạn cũng có sức chứa lớn, du khách không cần phải lo lắng tình trạng đông đúc, quá tải mùa du lịch.','5, Trường Sa, Quận Ngũ Hành Sơn, Đà Nẵng','0236 3981 234','hyatt@gmail.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(2,15,'Da Nang - Mikazuki Japanese Resorts & Spa',2,'Mang nét văn hóa tinh túy đậm đà Á Đông, Nhật Bản là nơi vẻ đẹp thiên nhiên, văn hóa và ẩm thực luôn được ca tụng. Với tinh thần lan tỏa tinh hoa văn hóa Nhật Bản trên đất Việt, Danang Mikazuki được lấy cảm hứng là nơi đất trời hội tụ và nhịp sống bắt đầu. Đến với chúng tôi để sẵn sàng cho một hành trình khám phá đầy thú vị với những giá trị chuẩn Nhật chưa từng có.
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(3,15,'Danang Golden Bay',2,'Danang Golden Bay – khách sạn 5 sao với những dịch vụ nghỉ dưỡng sang trọng và đẳng cấp. Sở hữu địa thế tuyệt vời nơi của dòng sông Hàn đổ ra biển Đông xanh ngắt, khách sạn Danang Golden Bay là điểm giao thoa hòa hợp giữa núi, trời và biển, là địa điểm lý tưởng khởi đầu cho hành trình khám phá thành phố Đà Nẵng năng động, vẻ đẹp quyến rũ của biển Đà Nẵng, Bán đảo Sơn Trà xanh tươi và khám phá hành trình di sản miền Trung từ Cố đô Huế cổ kính, Hội An thơ mộng hay thánh địa Mỹ Sơn huyền bí.','01, Lê Văn Duyệt, Quận Sơn Trà, Đà Nẵng','0236 3 878 999','info@dananggoldenbay.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(4,15,'Pullman Danang Beach Resort',2,'Pullman Danang Beach Resort là khách sạn thuộc tập đoàn ACCOR GROUP – tập đoàn khách sạn hàng đầu thế giới  mang tới du khách những trải nghiệm độc đáo và ý nghĩa với hơn 5.100 khách sạn và 10.000 nhà hàng trên 110 đất nước. Tập đoàn có một trong những hệ sinh thái đa dạng và tích hợp đầy đủ nhất ngành khách sạn với chuỗi các thương hiệu sang trọng và cao cấp, các dịch vụ tầm trung và phổ thông, những khái niệm phong cách sống độc đáo, các địa điểm giải trí, nhà hàng và quán bar, các khu bất động sản cho thương hiệu, không gian làm việc chia sẻ.','101, Võ Nguyên Giáp, Quận Ngũ Hành Sơn, Đà Nẵng','0236 395 8888','h8838@accor.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(5,15,'Altara Suites By RiYaz',2,'Với tiện nghi vượt bậc, Altara Suites là lựa chọn lý tưởng để tái tạo năng lượng và thư giãn. Khách sạn sở hữu những căn hộ được thiết kế theo phong cách hậu hiện đại, dưới bàn tay sáng tạo của kiến trúc sư nổi tiếng thế giới Salvador Perez Arroyo. Mỗi căn hộ kết nối khéo léo giữa không gian trong và ngoài khách sạn, du khách cỏ thể thưởng ngoạn khung cảnh tuyệt mỹ của thành phố Đà Nẵng với biển, núi, trời xung quanh.','120 Võ Nguyên Giáp, Phường Phước Mỹ, Quận Sơn Trà, Thành phố Đà Nẵng','0236 2687979','info@altarasuites.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(6,15,'Four Points by Sheraton Đà Nẵng',2,'Four Points by Sheraton Danang tọa lạc tại khu Bãi biển Mỹ Khê thuộc thành phố Đà Nẵng, nằm trong bán kính 2 km từ Cầu Sông Hàn và 2,3 km từ Cầu tàu Tình yêu. Khách sạn nằm cách Trung tâm thương mại Indochina Riverside 2,3 km.','120 Võ Nguyên Giáp, Street, Sơn Trà, Đà Nẵng','0236 3997 979','fourseason@gmail.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(7,15,'Melia Danang Beach Resort',2,'Nằm bên bãi biển Non Nước thơ mộng, nhìn ra bán đảo Sơn Trà và cù lao Chàm, Resort Bãi biển Meliá Đà Nẵng chính là một khu resort bãi biển nhiệt đới xinh đẹp điển hình.
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(8,15,'Vinpearl Condotel Riverfront Da Nang',2,'Vinpearl Condotel Riverfront Đà Nẵng là tòa khách sạn căn hộ có kiến trúc hiện đại với tầm nhìn panorama 360 độ độc đáo hướng trực diện sông Hàn, cầu Rồng và biển Mỹ Khê, ôm trọn vào lòng khung cảnh thành phố hoa lệ và sống động cùng nhiều tiện ích thời thượng bậc nhất như nhà hàng trên tầng cao sang trọng, hồ bơi vô cực, Vincharm Spa, phòng thể hình... Khách sạn được World Travel Awards – giải Oscar của ngành Du lịch vinh danh là "Khách sạn hướng sông hàng đầu Việt Nam" năm 2019.','341, Trần Hưng Đạo, Quận Sơn Trà, Đà Nẵng','1900 636699','callcenter@vinpearl.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(9,46,'Citadines Pearl Hội An',2,'Hội An được người dân khắp cả nước Việt Nam nói riêng và thế giới nói chung biết đến bởi vẻ đẹp truyền thống, bởi những nét đẹp thống nhất, cổ kính trong lối kiến trúc độc đáo thể hiện qua những ngôi nhà màu vàng hai tầng. Và để làm tô điểm thêm những nét độc đáo cho một vùng đất đặc biệt, “viên ngọc xanh” Citadines Pearl Hoi An đã hiện diện ở đây để được cùng trải qua với những vị khách đáng mến ngắm nhìn từng ngõ ngách nơi này. Khoảnh khắc này, hãy để Tico Travel cùng quý khách đi tham quan khu nghỉ dưỡng này để xem nơi này có thực sự đáng giá. ','Khối Phố An Bàng, Cẩm An, Thành Phố Hội An, Quảng Nam','0235 2208 888','citapearls@gmail.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(10,46,'Allegro Hội An . A Little Luxury Hotel & Spa',2,'Allegro Hoi An - Little Luxury Hotel & Spa tọa lạc tại thành phố Hội An thuộc tỉnh Quảng Nam, cách Chùa cầu Nhật Bản và Hội Quán Quảng Đông 400 m. Khách sạn có hồ bơi ngoài trời mở cửa quanh năm, đồng thời cung cấp miễn phí xe đạp cho khách sử dụng và dịch vụ đưa đón tới Phố Cổ Hội An và Bãi Biển An Bàng đối với khách lưu trú.','86, Trần Hưng Đạo, Thành Phố Hội An, Quảng Nam','0235 3529 999','allegro@gmail.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(11,46,'Le Pavillon Hội An Paradise Hotel & Spa',2,'Chào mừng bạn đến với Le Pavillon Hội An Paradise, khách sạn 4 sao nằm trên trục đường chính nối liền làng rau Trà Quế và Đô thị cổ Hội An. Le Pavilion Hội An Paradise là sự pha lẫn của lối kiến trúc đương đại và nét cổ điển của phương Tây, kết hợp với cách bày trí bằng đèn lồng mang đậm chất phố hội cổ kính nhưng đầy sự tinh tế. Với quy mô 50 phòng , nổi bật với hồ bơi trên tầng thượng sẽ mang đến cho bạn một trải nghiệm tuyệt vời khi vừa có thể đẵm mình trong làn nước mát vừa có thể ngắm hoàng hôn buông xuống nơi cuối chân trời. Tận hưởng sự hài hòa cảm giác giữa không gian hòa quyện với một thiên đường thanh bình.','508 Đ. Hai Bà Trưng, Cẩm Sơn, Hội An','0235 6276 666','resa-paradise@lepavillonhoiangroup.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(12,46,'Le Belhamy Hoi An Resort and Spa',2,'Chỗ nghỉ này cách bãi biển 1 phút đi bộ. Le Belhamy Resort & Spa nằm trên bãi biển riêng ở Bờ biển Hà My. Khu nghỉ mát nhiệt đới này có 2 hồ bơi ngoài trời và xe đưa đón miễn phí theo lịch trình đến Hội An.','Biển Hà My, Quảng Nam','0235 3941 888','lebelhamy@gmail.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(13,56,'Khách sạn Senna Huế',2,'Tọa lạc tại trung tâm thành phố Huế, chỉ cách Sân bay Phú Bài 14 km, với thiết kế hiện đại cổ điển, khách sạn Senna Huế cung cấp rất nhiều dịch vụ sang trọng. Ở đây, phép lịch sự và lòng tốt là nền tảng của lòng hiếu khách tuyệt vời, tạo ra một trải nghiệm độc đáo. Hãy tận hưởng kỳ nghỉ thoải mái tại những căn phòng sang trọng của chúng tôi.','7, Nguyễn Tri Phương, Thành Phố Huế, Thừa Thiên Huế','0234 3858 686','reservation@sennahue.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(14,56,'Khách Sạn Imperial Huế',2,'Nằm cách Cầu Trường Tiền chỉ 2 phút đi bộ, Imperial Hotel Hue có tầm nhìn ra Sông Hương thơ mộng và cung cấp chỗ nghỉ ấm cúng với máy điều hòa. Khách sạn có WiFi tốc độ cao miễn phí, hồ bơi ngoài trời, 3 địa điểm ăn uống, quán bar nhìn bao quát ra thành phố và chỗ đỗ xe miễn phí.','08 Hùng Vương, Phú Hội, Thành phố Huế, Thừa Thiên Huế','0234 3882 222','imperial@gmail.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(15,56,'Ancient Huế Garden Houses',2,'Ancient Hue Garden Houses is an elegant collection of five Imperial Garden Houses located in Kim Long village, adjacent to Hue’s Imperial Citadel. As an ideal gateway to the city’s magic, Ancient Hue Garden Houses offer a journey into Hue’s authentic mandarin residential area and lifestyle where tradition, poetry and spirit have been preserved over time.','47 Kiệt 104 Kim Long, Kim Long, Thành phố Huế, Thừa Thiên Huế','0234 3590 902','acienthotel@gmail.com');
insert into hotel(user_id,province_id,hotel_name,room_quantity,hotel_desc,hotel_address,hotel_phone,hotel_email) values(16,56,'Khách Sạn Emm Huế',2,'Tọa lạc tại khu trung tâm thành phố Huế, cách Sông Hương và Cầu Tràng Tiền 5 phút lái xe, khách sạn này cung cấp các phòng nghỉ lắp máy điều hòa, khu vườn, nhà hàng trong khuôn viên. Wi-Fi lẫn chỗ đỗ xe riêng đều được cung cấp miễn phí ngay trong khuôn viên.','15, Lý Thường Kiệt, Thành Phố Huế, Thừa Thiên Huế','0234 3828 255','emmhotel@gmail.com');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('17', '15', 'Khách sạn Sala Đà Nẵng Beach', 'Đỗ xe và Wi-Fi luôn miễn phí, vì vậy quý khách có thể giữ liên lạc, đến và đi tùy ý. Nằm ở vị trí trung tâm tại Phước Mỹ của Đà Nẵng, chỗ nghỉ này đặt quý khách ở gần các điểm thu hút và tùy chọn ăn uống thú vị. Hãy nhớ dành một chút thời gian để thăm thú The Marble Mountains cũng như My Khe Beach gần đó. Được xếp hạng 4.5 sao, chỗ nghỉ chất lượng cao này cho phép khách nghỉ sử dụng mát-xa, bể bơi ngoài trời và bồn tắm nước nóng ngay trong khuôn viên.', '36 - 38 Lâm Hoành, Phước Mỹ, Đà Nẵng, Việt Nam', '0236 3658 555', 'saladanang@gmail.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('18', '15', 'HAIAN Beach Hotel & Spa', 'Khách sạn cao tầng và hiện đại này nằm đối diện Bãi biển Mỹ Khê và cách Sân bay Quốc tế Đà Nẵng 6 km.', '278 Võ Nguyên Giáp, Bắc Mỹ Phú, Ngũ Hành Sơn, Đà Nẵng', '0236 2228 666', 'info@haianbeachhotelspa.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('19', '15', 'Khách sạn Stella Maris Beach Đà Nẵng', 'Chỗ nghỉ này cách bãi biển 1 phút đi bộ. Tọa lạc tại thành phố Đà Nẵng, cách Bãi biển Mỹ Khê 150 m, Stella Maris Beach Danang cung cấp chỗ nghỉ với nhà hàng, chỗ đỗ xe riêng miễn phí, hồ bơi ngoài trời và trung tâm thể dục. Nằm trong bán kính 2 km từ Bãi biển Bắc Mỹ An và 1,9 km từ Cầu khóa Tình yêu, chỗ nghỉ này có quầy bar cũng như sảnh khách chung. Chỗ nghỉ cung cấp dịch vụ lễ tân 24 giờ, dịch vụ phòng và dịch vụ thu đổi ngoại tệ cho khách.', '03 Võ Văn Kiệt, Phước Mỹ, Sơn Trà, Đà Nẵng', '0236 3555 657', 'stellamaris@gmail.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('20', '15', 'Khách sạn The Nalod Đà Nẵng', 'Được biết đến như một địa điểm quan trọng trên con đường di sản miền Trung, thành phố Đà Nẵng được bao bọc bởi 3 di sản văn hóa thế giới: Huế, Hội An, Mỹ Sơn. Nơi đây được thiên nhiên ưu ái với những bãi cát trắng xõa mình đầy tinh khôi bên cung đường biển dài bất tận, những ngọn núi, đám mây, cung đèo tưởng chừng như đơn giản nhưng cũng tạo nên một Đà Nẵng đầy chất thơ với phong cảnh hữu tình.', '192 Võ Nguyên Giáp, Phước Mỹ, Sơn Trà, Đà Nẵng', '0236 3913 999', 'info@nalod.com.vn', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('21', '15', 'Mercure Danang French Village Bana Hills', 'Chào mừng đến với Mercure Danang French Village Bana Hills, một khách sạn độc đáo bậc nhất Việt Nam.Tọa lạc trên đỉnh núi Bà Nà, một địa điểm giải trí phức hợp nổi tiếng ở Việt Nam và gần đây là nổi tiếng khắp thế giới qua hình ảnh Cầu Vàng, Mercure Danang French Village Bana Hills sở hữu quang cảnh tự nhiên hùng vĩ và tầm nhìn tuyệt vời về thành phố Đà Nẵng từ độ cao 1,487 mét so với mực nước biển.', 'An Son Hoa Son Commune, Hòa Vang, Đà Nẵng', '0236 3799 888', 'H8488-RE@accor.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('22', '15', 'Khách Sạn Alani Đà Nẵng', 'Aria Hotel Đà Nẵng nằm cách sân bay San bay Quoc te Da Nang 5 km, cung cấp dịch vụ phòng 24 giờ, quầy lễ tân 24 giờ và dịch vụ hướng dẫn khách. Bảo tàng Điêu khắc Chăm Đà Nẵng nằm trong bán kính 2 km từ khách sạn đáng yêu, trong khi Cầu Sông Hàn cách đó 2 km.', '134 - 136 - 138, Trần Bạch Đằng, Quận Ngũ Hành Sơn, Đà Nẵng', '0236 3887 227', 'alanidana@gmail.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('23', '15', 'Vinpearl Condotel Riverfront Da Nang', 'Vinpearl Condotel Riverfront Đà Nẵng là tòa khách sạn căn hộ có kiến trúc hiện đại với tầm nhìn panorama 360 độ độc đáo hướng trực diện sông Hàn, cầu Rồng và biển Mỹ Khê, ôm trọn vào lòng khung cảnh thành phố hoa lệ và sống động cùng nhiều tiện ích thời thượng bậc nhất như nhà hàng trên tầng cao sang trọng, hồ bơi vô cực, Vincharm Spa, phòng thể hình... Khách sạn được World Travel Awards – giải Oscar của ngành Du lịch vinh danh là \"Khách sạn hướng sông hàng đầu Việt Nam\" năm 2019.', '341 Đ. Trần Hưng Đạo, An Hải Bắc, Sơn Trà, Đà Nẵng', '0236 3642 888', 'callcenter@vinpearl.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('24', '15', 'Khách sạn Mường Thanh Grand Đà Nẵng', 'Là thành viên thứ 18 của chuỗi khách sạn tư nhân lớn nhất Việt Nam, khách sạn 4 sao Mường Thanh Grand Đà Nẵng tọa lạc tại vị trí đắc địa, nằm giữa một bên là sông Hàn thơ mộng, một bên là bãi biển đẹp nhất Miền Trung. Với hệ thống gồm 370 phòng nghỉ cao cấp và 08 căn hộ hạng sang cùng với các dịch vụ giải trí như bể bơi, phòng gym, spa, karaoke, nhà hàng, bar...Mường Thanh Grand Đà Nẵng sẽ mang đến một không gian nghỉ dưỡng tuyệt vời và thuận lợi cho những chuyến công tác của quý khách.', 'Số 962  Ngô Quyền, An Hải Tây, Sơn Trà, Đà Nẵng', '0236 3929 929', 'info@danang.muongthanh.vn', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('25', '15', 'Khách Sạn Novotel Danang Premier Han River', 'Đà Nẵng là một trong những thành phố sôi động và rực rỡ sắc màu nhất ở Việt Nam. Nơi đây tự hào khi được thiên nhiên ban tặng những bãi biển tuyệt đẹp với bờ cát mịn màng, tinh khôi nằm trải dài; có cảng hàng không Quốc tế hiện đại và nhiều địa điểm văn hóa nổi tiếng nằm trong nội thành, để du khách có thể di chuyển thuận lợi. Chính vì thế, sẽ không có gì đáng ngạc nhiên khi thành phố xinh đẹp này lại thu hút nhiều mối quan tâm, cũng như sự yêu mến nồng hậu của du khách trong nước và quốc tế đến như vậy.', '36 Bạch Đằng, Street, Hải Châu, Đà Nẵng', '0236 3929 999', 'H8287@accor.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('26', '15', 'Khách Sạn Monarque Đà Nẵng', 'Khi đi du lịch thì chúng ta không thể không nhắc đến các thiên đường nghĩ dưỡng tại thành phố xinh đẹp này và KHÁCH SẠN MONARQUE là một trong số đó. Với 75 phòng nghỉ được thiết kế trang nhã, độc đáo mang phong cách tân cổ điển – hơi hướng Châu Âu, phòng ốc mới hiện đại sẽ mang đến cho bạn một trải nghiệm mới mẻ.', '238 Võ Nguyên Giáp, Phước Mỹ, Sơn Trà, Đà Nẵng', '0236 3588 888', 'info@monarquehotel.vn', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('27', '15', 'Khách Sạn KOVA Diamond Đà Nẵng', 'Chỗ nghỉ này cách bãi biển 6 phút đi bộ. Tọa lạc tại thành phố Đà Nẵng, khách sạn KOVA Diamond Hotel Da Nang bên bãi biển này có nhà hàng ngay trong khuôn viên. Nơi đây có các phòng nghỉ hiện đại với truy cập Wi-Fi miễn phí.', '27 Võ Văn Kiệt, Street, Sơn Trà, Đà Nẵng', '0236 3933 999', 'kovadiamondana@gmail.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('28', '15', 'Khách Sạn Fivitel Đà Nẵng', 'Đà Nẵng, thành phố của những cây cầu nổi tiếng và con sông Hàn hiền hòa. Fivitlel tọa lạc bên cạnh cầu Trần Thị Lý, nghiêng bóng bên dòng sông Hàn thơ mộng hứa hẹn sẽ mang lại cho quý khách một hành trình tìm về cảm xúc yên bình và lãng mạn. Được thiết kế theo phong  cách Âu-Á kết hợp, vừa mang nét truyền thống Á Đông vừa mang dáng dấp  cổ điển của kiến trúc Châu Âu, Fivitel sẽ là một điểm nhấn làm tăng thêm vẽ quyến rũ của dòng sông Hàn xinh đẹp. Với trang thiết bị hiện đại bằng đồng mạ vàng sang trọng và đội ngũ nhân viên chuyên nghiệp, nhiệt tình, Chúng tôi cam kết đem đến cho Quý khách những trãi nghiệm tuyệt vời nhất và  một kỳ nghỉ hơn cả sự mong đợi.', '388 Trần Hưng Đạo, Q. Sơn Trà, Tp. Đà Nẵng', '0236 3555 688', 'info@fivitel.com.vn', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('29', '15', 'Sanouva Danang Hotel', 'Hãy tự hỏi tại sao khách du lịch đã lựa chọn Sanouva Danang Hotel khi đến thăm Đà Nẵng. Khách sạn là sự tổng hợp của những giá trị lý tưởng, sự thoải mái và thuận tiện như khung cảnh dành cho gia đình cùng với những tiện nghi được thiết kế cho những du khách như bạn.', '68 Phan Châu Trinh, Hải Châu 1, Hải Châu, Đà Nẵng', '0236 3823 468', 'sanouva@gmail.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('30', '15', 'Cicilia Hotels & Spa Danang', 'Tất cả phòng nghỉ tại Cicilia đều được thiết kế sang trọng, trang nhã và tầm nhìn hướng biển lãng mạn, mang đến cho du khách cảm giác thư thái và bình yên nhất. Tại đây, mỗi ngày, bạn có thể thư thái thưởng thức cafe, ẩm thực thượng hạng tại hệ thống nhà hàng cao cấp của khách sạn, tắm nắng bên hồ bơi chính(hồ bơi vô cực; chiêm ngưỡng cảnh mặt trời mọc mỗi sớm mai; tận hưởng cảm giác thư giãn tuyệt đối tại Cicilia Spa hay luyện tập trong những phòng thể thao hiện đại, tiện nghi.', '6-8-10, Đỗ Bá, Quận Ngũ Hành Sơn, Đà Nẵng', '0236 3747 888', 'info@ciciliadanang.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('31', '15', 'Khách Sạn Greenery', 'Cách Bảo tàng Điêu khắc Chăm Đà Nẵng 2 km, Greenery Hotel cung cấp bể bơi ngoài trời, khu đỗ xe miễn phí và hồ bơi trên sân thượng. Khách sạn này cũng cách Cầu Tình yêu Đà Nẵng 1.6 km.', '76, Hà Bổng, Quận Sơn Trà, Đà Nẵng', '0236 3825 888', 'greenery@gmail.com', '2');
INSERT INTO `hotelbookingdb`.`hotel` (`user_id`, `province_id`, `hotel_name`, `hotel_desc`, `hotel_address`, `hotel_phone`, `hotel_email`, `room_quantity`) VALUES ('32', '15', 'Khách sạn Sel De Mer Đà Nẵng', 'Cho dù là tận hưởng khung cảnh biển đầy cảm hứng hay thành phố thơ mộng, Sel de Mer đều mang lại cảm giác độc đáo cho du khách lưu trú. Với sự pha trộn cân bằng giữa lối kiến trúc tinh tế và đơn giản, mỗi phòng trong số 164 phòng và suite trang bị nội thất trang nhã đều được thiết kế với mục đích quan tâm đến sự thoải mái và tiện lợi của du khách. Khám phá thêm tại khách sạn boutique đạt tiêu chuẩn 5 sao của chúng tôi bằng cách nuông chiều bản thân tại Spa Annona, thưởng ngoạn quang cảnh Bán đảo Sơn Trà tuyệt đẹp tại hồ bơi vô cực. Không có nơi nào tuyệt vời hơn để ngắm nhìn toàn cảnh thành phố hoặc tận hưởng chút gió biển trong khi nhấm nháp những ly cocktail lạ miệng và món tapas chọn lọc tại The Speakeasy Bar & Tapas trên tầng thượng của Sel de Mer.', '92, Võ Nguyên Giáp, Quận Sơn Trà, Đà Nẵng, Việt Nam', '0236 3868 288', 'info@seldemerhotelandsuites.com', '2');
